import React from "react";
import bgHome from "../../assets/bg_title_home.png";
import Card from "../../components/Card/Card";
import data from "../../data/data.json"
import "./Home.scss";

function Home() {
    // Récupérer tous les ID des appartements à partir de data.json
    const apartmentIds = data.map((apartment) => apartment.id);

    const cardList = apartmentIds.map((id) => {
        const apartment = data.find((apartment) => apartment.id === id);

        if (apartment) {
            return <Card key={apartment.id} data={apartment} />;
        }
        return null;
    });

    return (
        <section id="homeContent">
            <div id="homeTitle">
                <img src={bgHome} alt="Fond de titre du site" />
                <h1>Chez vous, partout et ailleurs</h1>
            </div>
            <div id="cardList">
                {cardList}
            </div>
        </section>
    );
}

export default Home;
